void pretty_print_array(const uint8_t *array, size_t size)
{
    static uint32_t odd = 2;
    if (odd % 2 == 0)
    {
        printf("uint8_t raw_ex%d[] = {", odd / 2);
    }
    else
    {
        printf("uint8_t encoded_ex%d[] = {", odd / 2);
    }
    for (size_t i = 0; i < size; i++)
    {
        printf("0x%02x", array[i]);
        if (i < size - 1)
        {
            printf(", ");
        }
    }
    printf("};");
    printf("\n");
    odd++;
}
